CREATE OR REPLACE FUNCTION pls_obter_desc_mat_bras_tiss (cd_codigo_tiss_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
dt_inicio_vigencia_w	timestamp;


BEGIN

if (cd_codigo_tiss_p IS NOT NULL AND cd_codigo_tiss_p::text <> '') then
	select	/*+ INDEX (BRAPREC_I4)*/		max(e.dt_inicio_vigencia)
	into STRICT	dt_inicio_vigencia_w
	from	brasindice_preco e
	where	e.cd_tiss		= cd_codigo_tiss_p;

	if (dt_inicio_vigencia_w IS NOT NULL AND dt_inicio_vigencia_w::text <> '') then
		select	/*+ INDEX (BRAPREC_I4)*/			max(c.ds_medicamento)
		into STRICT 	ds_retorno_w
		from	brasindice_medicamento c,
			brasindice_preco a
		where	c.cd_medicamento 	= a.cd_medicamento
		and	a.dt_inicio_vigencia	= dt_inicio_vigencia_w
		and	a.cd_tiss 		= cd_codigo_tiss_p;
	else
		select	/*+ INDEX (BRAPREC_I4)*/			max(a.ds_medicamento)
		into STRICT	ds_retorno_w
		from	brasindice_medicamento a,
			brasindice_preco b
		where	a.cd_medicamento = b.cd_medicamento
		and	b.cd_tiss	 = cd_codigo_tiss_p;
	end if;
end if;

return substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
