CREATE OR REPLACE FUNCTION pls_obter_desc_mat_intercambio (nr_seq_auditoria_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_opme_w		varchar(255);
nr_seq_guia_w		bigint;
nr_seq_requisicao_w	bigint;


BEGIN

select	nr_seq_guia,
	nr_seq_requisicao
into STRICT	nr_seq_guia_w,
	nr_seq_requisicao_w
from	pls_auditoria
where	nr_sequencia	= nr_seq_auditoria_p;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
	begin
		select	substr(ds_opme,1,255)
		into STRICT	ds_opme_w
		from	ptu_pedido_aut_servico
		where	nr_seq_guia_mat	= nr_seq_material_p;
	exception
	when others then
		ds_opme_w	:= '';
	end;

	if (coalesce(ds_opme_w::text, '') = '') then
		begin
			select	substr(ds_opme,1,255)
			into STRICT	ds_opme_w
			from	ptu_pedido_compl_aut_serv
			where	nr_seq_guia_mat	= nr_seq_material_p;
		exception
		when others then
			ds_opme_w	:= '';
		end;
	end if;

elsif (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
	begin
		select	substr(ds_opme,1,255)
		into STRICT	ds_opme_w
		from	ptu_pedido_aut_servico
		where	nr_seq_req_mat	= nr_seq_material_p;
	exception
	when others then
		ds_opme_w	:= '';
	end;

	if (coalesce(ds_opme_w::text, '') = '') then
		begin
			select	substr(ds_opme,1,255)
			into STRICT	ds_opme_w
			from	ptu_pedido_compl_aut_serv
			where	nr_seq_req_mat	= nr_seq_material_p;
		exception
		when others then
			ds_opme_w	:= '';
		end;
	end if;
end if;

return	ds_opme_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
