CREATE OR REPLACE FUNCTION pls_obter_desc_parecer_disc ( nr_seq_motivo_aceito_p bigint, nr_seq_motivo_negado_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

begin
select	ds_motivo
into STRICT	ds_retorno_w
from	pls_motivo_glosa_aceita
where	nr_sequencia = nr_seq_motivo_aceito_p

union

SELECT	ds_motivo
from	pls_motivo_glosa_negada
where	nr_sequencia = nr_seq_motivo_negado_p;
exception
when others then
	ds_retorno_w := '';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
