SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_periodo_camara (nr_seq_periodo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_periodo_w		varchar(255)	:= null;
nr_ano_w		bigint;
nr_mes_w		bigint;
nr_camara_w		bigint;


BEGIN

if (nr_seq_periodo_p IS NOT NULL AND nr_seq_periodo_p::text <> '') then

	select	b.nr_ano,
		a.nr_mes,
		a.nr_camara
	into STRICT	nr_ano_w,
		nr_mes_w,
		nr_camara_w
	from	pls_camara_calendario b,
		pls_camara_calend_periodo a
	where	a.nr_seq_calendario	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_periodo_p;

	ds_periodo_w	:= nr_camara_w || 'º Câmara de ' || obter_mes_extenso(nr_mes_w,'C') || ' de ' || nr_ano_w;
end if;

return	ds_periodo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
