SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_periodo_cam_tit (nr_titulo_p bigint, ie_titulo_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter descrição do período da câmara em que o título se encontra.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [ ]  Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [X] Outros: Campo function no dicionário de dados.
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(4000);
nr_seq_lote_cam_w		bigint;
nr_seq_periodo_w		bigint;


BEGIN
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	if (ie_titulo_p = 'P') then
		select	max(a.nr_seq_lote_camara)
		into STRICT	nr_seq_lote_cam_w
		from	pls_titulo_lote_camara a
		where	a.nr_titulo_pagar = nr_titulo_p;

		if (nr_seq_lote_cam_w IS NOT NULL AND nr_seq_lote_cam_w::text <> '') then
			select	a.nr_seq_periodo
			into STRICT	nr_seq_periodo_w
			from	pls_lote_camara_comp a
			where 	a.nr_sequencia = nr_seq_lote_cam_w;

			if (nr_seq_periodo_w IS NOT NULL AND nr_seq_periodo_w::text <> '') then
				ds_retorno_w	:= substr(pls_obter_desc_periodo_camara(nr_seq_periodo_w),1,255);
			end if;
		end if;

	elsif (ie_titulo_p = 'R') then
		select	max(a.nr_seq_lote_camara)
		into STRICT	nr_seq_lote_cam_w
		from	pls_titulo_lote_camara a
		where	a.nr_titulo_receber = nr_titulo_p;

		if (nr_seq_lote_cam_w IS NOT NULL AND nr_seq_lote_cam_w::text <> '') then
			select	a.nr_seq_periodo
			into STRICT	nr_seq_periodo_w
			from	pls_lote_camara_comp a
			where 	a.nr_sequencia = nr_seq_lote_cam_w;

			if (nr_seq_periodo_w IS NOT NULL AND nr_seq_periodo_w::text <> '') then
				ds_retorno_w	:= substr(pls_obter_desc_periodo_camara(nr_seq_periodo_w),1,255);
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
