SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_periodo_cam (nr_seq_tit_lote_cam_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_periodo_w		varchar(255)	:= null;
nr_ano_w		bigint;
nr_mes_w		bigint;
nr_camara_w		bigint;
nr_seq_camara_w		bigint;


BEGIN

select	max(a.nr_seq_camara)
into STRICT	nr_seq_camara_w
from	pls_titulo_lote_camara b,
	pls_lote_camara_comp a
where	b.nr_seq_lote_camara	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_tit_lote_cam_p;

select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	pls_camara_compensacao a
where	a.nr_sequencia	= nr_seq_camara_w;

select	max(b.nr_ano),
	max(a.nr_mes),
	max(a.nr_camara)
into STRICT	nr_ano_w,
	nr_mes_w,
	nr_camara_w
from	pls_camara_calendario b,
	pls_camara_calend_periodo a
where	a.nr_seq_calendario	= b.nr_sequencia
and	b.nr_seq_camara		= nr_sequencia_w;

ds_periodo_w	:= nr_camara_w || 'º Câmara de ' || obter_mes_extenso(nr_mes_w,'C') || ' de ' || nr_ano_w;

return	ds_periodo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_periodo_cam (nr_seq_tit_lote_cam_p bigint) FROM PUBLIC;

