CREATE OR REPLACE FUNCTION pls_obter_desc_procedimento ( cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a descrição do procedimento
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_procedimento_w	PROCEDIMENTO.DS_PROCEDIMENTO%type;


BEGIN

begin
select	ds_procedimento
into STRICT	ds_procedimento_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= coalesce(ie_origem_proced_p, ie_origem_proced)  LIMIT 1;
exception
	when others then
	ds_procedimento_w	:= '';
end;

return	ds_procedimento_w;

end;
$body$
