SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_processo_jud ( nr_seq_processo_p text, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_segurado_w	bigint;
nr_seq_contrato_w	bigint;


BEGIN

if (coalesce(nr_seq_guia_p::text, '') = '') then
	select	ds_liminar
	into STRICT	ds_retorno_w
	from	processo_judicial_liminar
	where	nr_sequencia	= nr_seq_processo_p;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	begin
		select	nr_seq_segurado
		into STRICT	nr_seq_segurado_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_p;
	exception
	when others then
		nr_seq_segurado_w	:= 0;
	end;

	begin
		select	nr_seq_contrato
		into STRICT	nr_seq_contrato_w
		from	pls_segurado
		where	nr_sequencia	= nr_seq_segurado_w;
	exception
	when others then
		nr_seq_contrato_w	:= 0;
	end;

	begin
		select	ds_liminar
		into STRICT	ds_retorno_w
		from	processo_judicial_liminar
		where	nr_sequencia 	= nr_seq_processo_p
		and (nr_seq_segurado	= nr_seq_segurado_w
		or	nr_seq_contrato		= nr_seq_contrato_w)
		and	ie_estagio	= 2
		and	clock_timestamp()	between coalesce(dt_inicio_validade,dt_liberacao) and fim_dia(coalesce(dt_fim_validade,clock_timestamp()));
	exception
	when others then
		ds_retorno_w	:= '';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
