CREATE OR REPLACE FUNCTION pls_obter_desc_proc_anexo (nr_seq_plano_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_procedimento_w	procedimento.ds_procedimento%type;
cd_procedimento_w	procedimento.cd_procedimento%type;
ie_origem_proced_w	procedimento.ie_origem_proced%type;


BEGIN

begin
	select	cd_procedimento,
		ie_origem_proced
	into STRICT 	cd_procedimento_w,
		ie_origem_proced_w
	from	pls_guia_plano_proc
	where	nr_sequencia = nr_seq_plano_proc_p;
exception
when others then
	cd_procedimento_w	:= null;
	ie_origem_proced_w	:= null;
end;

if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '')	and (ie_origem_proced_w IS NOT NULL AND ie_origem_proced_w::text <> '') then
	begin
		select	ds_procedimento
		into STRICT	ds_procedimento_w
		from	procedimento
		where	cd_procedimento = cd_procedimento_w
		and	ie_origem_proced = ie_origem_proced_w;
	exception
	when others then
		ds_procedimento_w	:= '';
	end;
end if;

return	ds_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
