CREATE OR REPLACE FUNCTION pls_obter_desc_proc_mat ( cd_proc_mat_p bigint, ie_origem_proced_p bigint, ie_param_p bigint) RETURNS varchar AS $body$
DECLARE


/*
ie_param_p
P = Procedimento
M = Material
*/
ie_retorno_w		varchar(255);


BEGIN



if (ie_param_p	in (2,3)) then
	select	substr(obter_descricao_padrao('PLS_MATERIAL','DS_MATERIAL',cd_proc_mat_p),1,255)
	into STRICT	ie_retorno_w
	;
else
	select	substr(obter_descricao_procedimento(cd_proc_mat_p,ie_origem_proced_p),1,255)
	into STRICT	ie_retorno_w
	;
end if;

return	ie_retorno_w;

end;
$body$
