CREATE OR REPLACE FUNCTION pls_obter_desc_proc_partic ( nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w		bigint;
ir_origem_proced_w		bigint;
ds_retorno_w			varchar(255);


BEGIN

begin
	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ir_origem_proced_w
	from	pls_oc_regra_proc_partic
	where	nr_sequencia = nr_seq_regra_p;
exception
when others then
	cd_procedimento_w := null;
end;

ds_retorno_w := cd_procedimento_w || ' - ' || substr(Obter_Descricao_Procedimento(cd_procedimento_w,ir_origem_proced_w),1,255);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
