CREATE OR REPLACE FUNCTION pls_obter_desc_proc_processo ( nr_seq_conta_p pls_processo_procedimento.nr_seq_conta%type) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w 	varchar(4000);
ds_proc_w	varchar(4000);
cd_proc_w	pls_processo_procedimento.cd_procedimento%type;
qt_proc_w	integer;
qt_linha_w	smallint;

c01 CURSOR FOR
	SELECT	substr(obter_descricao_procedimento(cd_procedimento, ie_origem_proced), 1, 255),
		cd_procedimento cd_proc
	from	pls_processo_procedimento
	where	nr_seq_conta = nr_seq_conta_p;

BEGIN
ds_proc_w	:= '';
ds_retorno_w	:= '';
qt_linha_w	:= 1;

select	count(1)
into STRICT	qt_proc_w
from	pls_processo_procedimento
where	nr_seq_conta = nr_seq_conta_p;

if (qt_proc_w > 0) then
	if (qt_proc_w > 1) then
		open c01;
		loop
		fetch c01 into
			ds_proc_w,
			cd_proc_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

				if (qt_linha_w  = qt_proc_w) then
					ds_retorno_w := substr(ds_retorno_w || cd_proc_w || ' - ' || ds_proc_w, 1, 4000);
				else
					ds_retorno_w := substr(ds_retorno_w || cd_proc_w || ' - ' || ds_proc_w || '; ', 1, 4000);
				end if;

				qt_linha_w := qt_linha_w + 1;
			end;
		end loop;
		close c01;
	else
		select	substr(cd_procedimento || ' - ' || obter_descricao_procedimento(cd_procedimento, ie_origem_proced), 1, 255)
		into STRICT	ds_retorno_w
		from	pls_processo_procedimento
		where	nr_seq_conta = nr_seq_conta_p;
	end if;


end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
