CREATE OR REPLACE FUNCTION pls_obter_desc_rede_ref_prod ( cd_rede_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255) := '';


BEGIN

	if (cd_rede_p IS NOT NULL AND cd_rede_p::text <> '') then
		select	max(ds_rede)
		into STRICT	ds_retorno_w
		from	ptu_rede_referenciada
		where	cd_estabelecimento = cd_estabelecimento_p
		and	cd_rede		   = cd_rede_p;
	end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
