CREATE OR REPLACE FUNCTION pls_obter_desc_regra_atu_cad ( nr_seq_regra_atu_cad_p bigint) RETURNS varchar AS $body$
DECLARE


ds_regra_w		varchar(255);


BEGIN
begin
	select 	substr(ds_regra,1,255)
	into STRICT 	ds_regra_w
	from 	pls_regra_atualizacao_cad
	where	nr_sequencia 	= nr_seq_regra_atu_cad_p;
exception
when others then
	ds_regra_w	:= null;
end;

return	ds_regra_w;

end;
$body$
