CREATE OR REPLACE FUNCTION pls_obter_desc_regra (ie_tipo_regra_p bigint) RETURNS varchar AS $body$
DECLARE



ds_dominio_w		varchar(255);
vl_dominio_w		varchar(255);

BEGIN

if (ie_tipo_regra_p IS NOT NULL AND ie_tipo_regra_p::text <> '') then

	select 	vl_dominio cd,
		ds_expressao ds
	into STRICT	vl_dominio_w,
		ds_dominio_w
	from 	valor_dominio_v
	where 	cd_dominio = 3606
	and 	vl_dominio = ie_tipo_regra_p;

end if;

return	ds_dominio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
