CREATE OR REPLACE FUNCTION pls_obter_desc_sca_seg (nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_sca_w	varchar(2000);
DS_NOME_SCA_W	varchar(2000);

C01 CURSOR FOR
	SELECT	a.ds_plano
	from	pls_sca_vinculo	b,
		pls_plano	a
	where	b.nr_seq_plano		= a.nr_sequencia
	and	b.nr_seq_segurado	= nr_seq_segurado_p
	and	coalesce(b.dt_fim_vigencia::text, '') = ''
	group by a.ds_plano;


BEGIN
open C01;
loop
fetch C01 into
	ds_sca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	DS_NOME_SCA_W := ds_sca_w||', '||DS_NOME_SCA_W;
	ds_sca_w	:= '';
	end;
end loop;
close C01;

return substr(DS_NOME_SCA_W,1,length(DS_NOME_SCA_W)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
