CREATE OR REPLACE FUNCTION pls_obter_desc_servico ( cd_procedimento_p procedimento.cd_procedimento%type, ie_origem_proced_p procedimento.ie_origem_proced%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se a conta se encaixa nos filtros específicos de conta para
as ocorrências combinadas de conta médica.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	procedimento.ds_procedimento%type;


BEGIN
if	(cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '' AND ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then

	select	max(ds_procedimento)
	into STRICT	ds_retorno_w
	from	procedimento
	where	cd_procedimento 	= cd_procedimento_p
	and	ie_origem_proced 	= ie_origem_proced_p
	and	ie_classificacao 	= '2';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
