SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_subestipulante ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter nome do subestipulante
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ X ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_retorno_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);


BEGIN

select	cd_pessoa_fisica,
	cd_cgc
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w
from	pls_sub_estipulante
where	nr_sequencia	= nr_sequencia_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	ds_retorno_w	:= 'PF - ' || substr(obter_nome_pf(cd_pessoa_fisica_w),1,60);
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	ds_retorno_w	:= 'PJ - ' || substr(obter_razao_social(cd_cgc_w),1,80);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_subestipulante ( nr_sequencia_p bigint) FROM PUBLIC;

