SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_tabela_linear ( nr_seq_tabela_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
	D - Descrição
	C - Código
*/
ds_retorno_w		varchar(255);
nm_tabela_w		varchar(255);


BEGIN

if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then
	select	nm_tabela
	into STRICT	nm_tabela_w
	from	pls_tabela_preco
	where	nr_sequencia	= nr_seq_tabela_p;

	if (upper(nm_tabela_w) like '%LINEAR%') then
		if (ie_opcao_p = 'D') then
			ds_retorno_w	:= 'Linear';
		elsif (ie_opcao_p = 'C') then
			ds_retorno_w	:= 'L';
		end if;
	else
		if (ie_opcao_p = 'D') then
			ds_retorno_w	:= 'Faixa etária';
		elsif (ie_opcao_p = 'C') then
			ds_retorno_w	:= 'F';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
