SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_tema (nr_seq_tema_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	'N' - Nome do tema
	'D' - Descrição do tema
*/
ds_tema_w		varchar(4000);
nm_tema_w		varchar(255);
ds_retorno_w		varchar(4000);


BEGIN

if (nr_seq_tema_p IS NOT NULL AND nr_seq_tema_p::text <> '') then

	select	ds_tema,
		nm_tema
	into STRICT	ds_tema_w,
		nm_tema_w
	from	pls_instrumento_juridico
	where	nr_sequencia	= nr_seq_tema_p;

	if (ie_opcao_p = 'N') then
		ds_retorno_w	:= nm_tema_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_tema_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
