CREATE OR REPLACE FUNCTION pls_obter_desc_tipo_faturam (ie_tipo_faturamento_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (upper(ie_tipo_faturamento_p) = 'T') then
	ds_retorno_w := 'Total';
elsif (upper(ie_tipo_faturamento_p) = 'P') then
	ds_retorno_w := 'Parcial';
elsif (upper(ie_tipo_faturamento_p) = 'C') then
	ds_retorno_w := 'Complementar';
elsif (upper(ie_tipo_faturamento_p) = 'F') then
	ds_retorno_w := 'Final';
end if;

return	ds_retorno_w;

end;
