CREATE OR REPLACE FUNCTION pls_obter_desc_tipo_guia_prest ( nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_auxiliar_w		varchar(2000);

C01 CURSOR FOR
	SELECT	a.ds_tipo_guia
	from	pls_tipo_guia_med_partic	b,
		pls_tipo_guia_medico		a
	where	b.nr_seq_tipo_guia	= a.nr_sequencia
	and	b.nr_seq_prestador	= nr_seq_prestador_p
	and	b.ie_entra_portal	= 'S'
	and	b.ie_entra_guia_medico	= 'S'
	group by a.ds_tipo_guia
	order by a.ds_tipo_guia;


BEGIN
open C01;
loop
fetch C01 into
	ds_auxiliar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w || ds_auxiliar_w || ' \ ';
	end;
end loop;
close C01;


return	substr(ds_retorno_w, 1, length(ds_retorno_w) - 2 );

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_tipo_guia_prest ( nr_seq_prestador_p bigint) FROM PUBLIC;

