CREATE OR REPLACE FUNCTION pls_obter_desc_tipo_internacao (nr_seq_clinica_p pls_clinica.nr_sequencia%type, cd_clinica_p pls_clinica.cd_clinica%type default null) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a descrição do tipo de internação
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ds_tipo_internacao_w		varchar(255);


BEGIN
if (cd_clinica_p IS NOT NULL AND cd_clinica_p::text <> '') then
	select	max(ds_clinica)
	into STRICT	ds_tipo_internacao_w
	from	pls_clinica
	where	cd_clinica = cd_clinica_p;
else
	select	max(ds_clinica)
	into STRICT	ds_tipo_internacao_w
	from	pls_clinica
	where	nr_sequencia = nr_seq_clinica_p;
end if;

return ds_tipo_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
