SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_desc_tipo_pj ( cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
qt_registros_w			smallint;
cd_tipo_pessoa_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	pls_cooperado
where	cd_cgc	= cd_cgc_p;

if (qt_registros_w	> 0) then
	ds_retorno_w	:= 'Cooperado';
elsif (qt_registros_w	= 0) then
	select	cd_tipo_pessoa
	into STRICT	cd_tipo_pessoa_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_p;

	select 	ds_tipo_pessoa
	into STRICT	ds_retorno_w
	from 	tipo_pessoa_juridica
	where 	cd_tipo_pessoa	= cd_tipo_pessoa_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_desc_tipo_pj ( cd_cgc_p text) FROM PUBLIC;

