CREATE OR REPLACE FUNCTION pls_obter_despesa_mat ( nr_seq_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(50);


BEGIN

if (ie_opcao_p = 'D') then
	select	CASE WHEN ie_tipo_despesa=1 THEN  'Gases medicinais' WHEN ie_tipo_despesa=2 THEN  'Medicamentos' WHEN ie_tipo_despesa=3 THEN  'Materiais' WHEN ie_tipo_despesa=7 THEN  'OPM' END
	into STRICT	ds_retorno_w
	from	pls_material
	where	nr_sequencia = nr_seq_material_p;
elsif (ie_opcao_p = 'S') then
	select	ie_tipo_despesa
	into STRICT	ds_retorno_w
	from	pls_material
	where	nr_sequencia = nr_seq_material_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
