CREATE OR REPLACE FUNCTION pls_obter_destino_corresp_pag ( nr_seq_pagador_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255):= '';
nr_seq_destino_corresp_w	bigint;



BEGIN

if (coalesce(nr_seq_pagador_p,0) > 0) then
	select	coalesce(nr_seq_destino_corresp,0)
	into STRICT	nr_seq_destino_corresp_w
	from 	pls_contrato_pagador
	where	nr_sequencia = nr_seq_pagador_p;

	if (coalesce(nr_seq_destino_corresp_w,0)>0) then
		select	x.ds_destino
		into STRICT	ds_retorno_w
		from	pls_destino_corresp	x
		where	nr_sequencia = nr_seq_destino_corresp_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
