CREATE OR REPLACE FUNCTION pls_obter_devolucao_carteira ( nr_seq_conta_p bigint, nr_seq_segurado_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_usuario_plano_w		varchar(30);
ie_retorno_w			varchar(2) := 'N';
nr_seq_segurado_w		bigint;
dt_devolucao_w			timestamp;
dt_validade_carteira_w		timestamp;
nr_via_solicitacao_w		bigint;


BEGIN
if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then

	select	max(dt_validade_carteira),
		max(nr_via_solicitacao),
		max(cd_usuario_plano)
	into STRICT	dt_validade_carteira_w,
		nr_via_solicitacao_w,
		cd_usuario_plano_w
	from	pls_segurado_carteira
	where	nr_seq_segurado		= nr_seq_segurado_p;

	select	to_char(max(b.dt_devolucao),'dd/mm/yyyy')
	into STRICT	dt_devolucao_w
	from	pls_cart_lote_devolucao	b,
		pls_carteira_devolucao	a
	where	a.nr_seq_lote		= b.nr_sequencia
	and	a.cd_usuario_plano	= cd_usuario_plano_w
	and	((trunc(coalesce(dt_validade_carteira,dt_validade_carteira_w),'dd')	= dt_validade_carteira_w) or (coalesce(dt_validade_carteira_w::text, '') = ''))
	and	coalesce(nr_via,nr_via_solicitacao_w)				= nr_via_solicitacao_w;

	if (dt_devolucao_w IS NOT NULL AND dt_devolucao_w::text <> '') and (dt_devolucao_w <= dt_referencia_p) then
		ie_retorno_w := 'D';
	else
		ie_retorno_w := 'ND';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
