SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dias_caren_benef_ant ( nr_seq_segurado_p bigint, nr_seq_tipo_carencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_registros_w		bigint;
qt_dias_w		bigint;


BEGIN

qt_dias_w	:= -1;

select	count(1)
into STRICT	qt_registros_w
from	PLS_GRUPO_CARENCIA	d,
	pls_tipo_carencia	c,
	pls_segurado		b,
	pls_carencia		a
where	a.nr_seq_segurado 	= b.nr_sequencia
and	c.nr_seq_grupo		= d.nr_sequencia
and	a.nr_seq_grupo_carencia	= d.nr_sequencia
and	c.nr_sequencia		= nr_seq_tipo_carencia_p
and	b.nr_sequencia		= nr_seq_segurado_p
and	a.ie_cpt		= 'N';

if (qt_registros_w > 0) then
	select	max(QT_DIAS)
	into STRICT	qt_dias_w
	from	PLS_GRUPO_CARENCIA	d,
		pls_tipo_carencia	c,
		pls_segurado		b,
		pls_carencia		a
	where	a.nr_seq_segurado 	= b.nr_sequencia
	and	c.nr_seq_grupo		= d.nr_sequencia
	and	a.nr_seq_grupo_carencia	= d.nr_sequencia
	and	c.nr_sequencia		= nr_seq_tipo_carencia_p
	and	b.nr_sequencia		= nr_seq_segurado_p
	and	a.ie_cpt		= 'N';
end if;

return	qt_dias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
