SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dias_efetivacao ( nr_seq_cliente_p bigint, nr_seq_solicitacao_p bigint) RETURNS bigint AS $body$
DECLARE


dt_aprovacao_w		timestamp;
nr_seq_canal_ativo_w	bigint;
qt_dias_efetivacao_w	integer;
qt_dias_retorno_w	bigint;
dt_inicio_vigencia_w	timestamp;
dt_efetivacao_w		timestamp;
qt_reg_efetivacao_w	bigint;


BEGIN

if (coalesce(nr_seq_cliente_p,0) <> 0) then
	select	count(1)
	into STRICT	qt_reg_efetivacao_w
	from	pls_comercial_cliente
	where	nr_sequencia	= nr_seq_cliente_p
	and	(dt_efetivacao IS NOT NULL AND dt_efetivacao::text <> '')  LIMIT ALL;

	select	dt_efetivacao,
		dt_aprovacao
	into STRICT	dt_efetivacao_w,
		dt_aprovacao_w
	from	pls_comercial_cliente
	where	nr_sequencia	= nr_seq_cliente_p;

	if (qt_reg_efetivacao_w	> 0) then
		qt_dias_retorno_w	:= trunc(dt_efetivacao_w,'dd') - trunc(clock_timestamp(),'dd');

		if (qt_dias_retorno_w < 0) then
			qt_dias_retorno_w := 0;
		end if;
	else
		select	max(qt_dias_efetivacao)
		into STRICT	qt_dias_efetivacao_w
		from	pls_solicitacao_vendedor
		where	nr_seq_cliente	= nr_seq_cliente_p
		and	coalesce(dt_fim_vigencia::text, '') = ''  LIMIT 1;

		if (qt_dias_efetivacao_w IS NOT NULL AND qt_dias_efetivacao_w::text <> '') then
			select	((dt_aprovacao_w + qt_dias_efetivacao_w) - (trunc(clock_timestamp()) - dt_aprovacao_w)) - dt_aprovacao_w
			into STRICT	qt_dias_retorno_w
			;

			if (qt_dias_retorno_w < 0) then
				qt_dias_retorno_w := 0;
			end if;
		end if;
	end if;
elsif (coalesce(nr_seq_solicitacao_p,0) <> 0) then
	select	count(1)
	into STRICT	qt_reg_efetivacao_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_seq_solicitacao_p
	and	(dt_efetivacao IS NOT NULL AND dt_efetivacao::text <> '')  LIMIT ALL;

	if (qt_reg_efetivacao_w	> 0) then
		select	dt_efetivacao
		into STRICT	dt_efetivacao_w
		from	pls_solicitacao_comercial
		where	nr_sequencia	= nr_seq_solicitacao_p;

		qt_dias_retorno_w	:= trunc(dt_efetivacao_w,'dd') - trunc(clock_timestamp(),'dd');

		if (qt_dias_retorno_w < 0) then
			qt_dias_retorno_w := 0;
		end if;
	else
		select	max(qt_dias_efetivacao),
			max(dt_inicio_vigencia)
		into STRICT	qt_dias_efetivacao_w,
			dt_inicio_vigencia_w
		from	pls_solicitacao_vendedor
		where	nr_seq_solicitacao	= nr_seq_solicitacao_p
		and	coalesce(dt_fim_vigencia::text, '') = ''  LIMIT 1;

		if (qt_dias_efetivacao_w IS NOT NULL AND qt_dias_efetivacao_w::text <> '') then
			select (dt_inicio_vigencia_w + qt_dias_efetivacao_w) - trunc(clock_timestamp())
			into STRICT	qt_dias_retorno_w
			;

			if (qt_dias_retorno_w < 0) then
				qt_dias_retorno_w := 0;
			end if;
		end if;
	end if;
end if;

return	qt_dias_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
