SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dias_inadimplencia ( nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retorna quantidade de dias de atrazo da mensalidade
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_cgc_w			varchar(14);
cd_pessoa_fisica_w		varchar(10);
nr_seq_pagador_w		bigint;
qt_dias_retorno_w		bigint	:= 0;
qt_dias_ret_pag_w		bigint	:= 0;
qt_dias_ret_mens_w		bigint	:= 0;


BEGIN
begin
select	nr_seq_pagador
into STRICT	nr_seq_pagador_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_pagador_w := 0;
end;

if (nr_seq_pagador_w <> 0) then
	/*select	cd_cgc,
		cd_pessoa_fisica
	into	cd_cgc_w,
		cd_pessoa_fisica_w
	from	pls_contrato_pagador
	where	nr_sequencia	= nr_seq_pagador_w;*/
	select	max(clock_timestamp() - dt_pagamento_previsto)
	into STRICT	qt_dias_ret_pag_w
	from	titulo_receber	a
	where	ie_pls		= 'S'
	and	coalesce(dt_liquidacao::text, '') = ''
	and	dt_pagamento_previsto < clock_timestamp()
	and	nr_seq_pagador	= nr_seq_pagador_w
	and	ie_origem_titulo = '3'
	and	a.ie_situacao	= '1';

	/* Select adicional para buscar apenas os casos onde ainda não gravava o NR_SEQ_PAGADOR no título */

	select	max(clock_timestamp() - a.dt_pagamento_previsto)
	into STRICT	qt_dias_ret_mens_w
	from	pls_mensalidade b,
		titulo_receber	a
	where	a.nr_seq_mensalidade	= b.nr_sequencia
	and	a.ie_pls		= 'S'
	and	coalesce(a.dt_liquidacao::text, '') = ''
	and	a.dt_pagamento_previsto < clock_timestamp()
	and	b.nr_seq_pagador	= nr_seq_pagador_w
	and	a.ie_origem_titulo = '3'
	and	coalesce(a.nr_seq_pagador::text, '') = ''
	and	a.ie_situacao = '1';
end if;

if (qt_dias_ret_pag_w > 0) then
	qt_dias_retorno_w	:= qt_dias_ret_pag_w;

	/* Tratamento para se achar por mensalidade com quantidade de dias maior, usar este */

	if (qt_dias_ret_mens_w > qt_dias_ret_pag_w) then
		qt_dias_retorno_w	:= qt_dias_ret_mens_w;
	end if;
end if;

return	qt_dias_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dias_inadimplencia ( nr_seq_segurado_p bigint) FROM PUBLIC;

