CREATE OR REPLACE FUNCTION pls_obter_dif_data_horario ( dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_dif_w		varchar(15);
qt_hora_w		bigint;
qt_min_w		bigint;
qt_seg_w		bigint;


BEGIN
ds_dif_w		:= '00:00:00';

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (dt_final_p > dt_inicial_p) then
	qt_Seg_w	:= trunc((dt_final_p - dt_inicial_p) * 86400);
	qt_hora_w	:= trunc(qt_seg_w / 3600);
	qt_seg_w	:= qt_seg_w - (qt_hora_w * 3600);
	qt_min_w	:= trunc(qt_seg_w / 60);
	qt_seg_w	:= qt_seg_w - (qt_min_w * 60);
	ds_dif_w	:= to_char(qt_hora_w,'FM999900') || ':' || to_char(qt_min_w,'FM00') || ':' || to_char(qt_seg_w,'FM00');
end if;

return ds_dif_w;

end;
