CREATE OR REPLACE FUNCTION pls_obter_dif_procedimento (nr_seq_procedimento bigint) RETURNS bigint AS $body$
DECLARE


vl_procedimento_w	pls_processo_procedimento.vl_procedimento%type;
vl_proc_calculado_w	pls_processo_procedimento.vl_proc_calculado%type;
ds_retorno_w		double precision;


BEGIN

select	vl_procedimento,
	vl_proc_calculado
into STRICT	vl_procedimento_w,
	vl_proc_calculado_w
from	pls_processo_procedimento
where	nr_sequencia = nr_seq_procedimento;

if 	(vl_procedimento_w IS NOT NULL AND vl_procedimento_w::text <> '' AND vl_proc_calculado_w IS NOT NULL AND vl_proc_calculado_w::text <> '') then
	ds_retorno_w := vl_procedimento_w - vl_proc_calculado_w;
elsif ((vl_procedimento_w IS NOT NULL AND vl_procedimento_w::text <> '') and (coalesce(vl_proc_calculado_w::text, '') = '')) then
	ds_retorno_w := vl_procedimento_w;
elsif ((coalesce(vl_procedimento_w::text, '') = '') and (vl_proc_calculado_w IS NOT NULL AND vl_proc_calculado_w::text <> '')) then
	ds_retorno_w := vl_proc_calculado_w;
end if;

return	ds_retorno_w;

end;
$body$
