CREATE OR REPLACE FUNCTION pls_obter_digito_banc_prest ( nr_seq_pessoa_fisica_conta_p bigint, nr_seq_pessoa_jur_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);

/* IE_OPCAO_P
DA - Dígito agência
DC - Dígito conta */
BEGIN
/* Prestador - Pessoa física */

if (nr_seq_pessoa_fisica_conta_p IS NOT NULL AND nr_seq_pessoa_fisica_conta_p::text <> '') then
	if (upper(ie_opcao_p) = 'DA') then
		select	max(ie_digito_agencia)
		into STRICT	ds_retorno_w
		from	pessoa_fisica_conta
		where	nr_sequencia	= nr_seq_pessoa_fisica_conta_p;

	elsif (upper(ie_opcao_p) = 'DC') then
		select	max(nr_digito_conta)
		into STRICT	ds_retorno_w
		from	pessoa_fisica_conta
		where	nr_sequencia	= nr_seq_pessoa_fisica_conta_p;
	end if;

/* Prestador - Pessoa jurídica */

elsif (nr_seq_pessoa_jur_conta_p IS NOT NULL AND nr_seq_pessoa_jur_conta_p::text <> '') then
	if (upper(ie_opcao_p) = 'DA') then
		select	max(ie_digito_agencia)
		into STRICT	ds_retorno_w
		from	pessoa_juridica_conta
		where	nr_sequencia	= nr_seq_pessoa_jur_conta_p;

	elsif (upper(ie_opcao_p) = 'DC') then
		select	max(nr_digito_conta)
		into STRICT	ds_retorno_w
		from	pessoa_juridica_conta
		where	nr_sequencia	= nr_seq_pessoa_jur_conta_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
