CREATE OR REPLACE FUNCTION pls_obter_digito_conta_banco ( nr_seq_conta_banco_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);

/* IE_OPCAO_P
DA - Dígito agência
DC - Dígito conta */
BEGIN
if (nr_seq_conta_banco_p IS NOT NULL AND nr_seq_conta_banco_p::text <> '') then
  if (upper(ie_opcao_p) = 'DA') then

    select max(a.ie_digito)
    into STRICT   ds_retorno_w
    from   agencia_bancaria a,
           banco_estabelecimento b
    where  a.cd_agencia_bancaria = b.cd_agencia_bancaria
    and    b.nr_sequencia = nr_seq_conta_banco_p;

  elsif (upper(ie_opcao_p) = 'DC') then

    select max(ie_digito_conta)
    into STRICT   ds_retorno_w
    from   banco_estabelecimento
    where  nr_sequencia = nr_seq_conta_banco_p;

  end if;
end if;

return  ds_retorno_w;

end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
