CREATE OR REPLACE FUNCTION pls_obter_dir_arquivos_scs ( ie_opcao_dir_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);


BEGIN


if (nm_tabela_p in ('PTU_PEDIDO_AUTORIZACAO','PTU_PEDIDO_COMPL_AUT','PTU_PEDIDO_INSISTENCIA','PTU_RESPOSTA_AUDITORIA','PTU_CANCELAMENTO','PTU_REQUISICAO_ORDEM_SERV')) then
	if (ie_opcao_dir_p	= 1) then
		select	ds_dir_backup_saida_cli
		into STRICT	ds_retorno_w
		from	pls_parametros_scs;
	elsif (ie_opcao_dir_p	= 2) then
		select	ds_dir_backup_entrada_serv
		into STRICT	ds_retorno_w
		from	pls_parametros_scs;
	end if;
elsif (nm_tabela_p in ('PTU_RESPOSTA_AUTORIZACAO','PTU_CONFIRMACAO','PTU_RESPOSTA_REQ_ORD_SERV','PTU_ERRO_INESPERADO')) then
	if (ie_opcao_dir_p	= 1) then
		select	ds_dir_backup_entrada_cli
		into STRICT	ds_retorno_w
		from	pls_parametros_scs;
	elsif (ie_opcao_dir_p	= 2) then
		select	ds_dir_backup_saida_serv
		into STRICT	ds_retorno_w
		from	pls_parametros_scs;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
