CREATE OR REPLACE FUNCTION pls_obter_doc_obs_glosa_oc ( ie_tipo_p text, nr_seq_glosa_oc_p bigint, ie_doc_observacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

if (ie_tipo_p = 'G') then

	select	CASE WHEN ie_doc_observacao_p='D' THEN  ds_documentacao  ELSE ds_observacao END
	into STRICT	ds_retorno_w
	from	pls_conta_glosa
	where	nr_sequencia = nr_seq_glosa_oc_p;
else

	select	CASE WHEN ie_doc_observacao_p='D' THEN  ds_documentacao  ELSE ds_observacao END
	into STRICT	ds_retorno_w
	from	pls_ocorrencia_benef
	where	nr_sequencia = nr_seq_glosa_oc_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
