CREATE OR REPLACE FUNCTION pls_obter_dscancelamento_def ( nr_motivo_cancelamento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(180);


BEGIN

select	coalesce(max(ds_motivo),'')
