CREATE OR REPLACE FUNCTION pls_obter_ds_aux_excedeu ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_auxiliares_p bigint) RETURNS varchar AS $body$
DECLARE


vl_posicao_auxiliar_w		bigint;
ie_auxiliar_w			varchar(1);
ds_retorno_w			varchar(4000);
ds_grau_participacao_w		pls_grau_participacao.ds_grau_participacao%type;

C01 CURSOR FOR
	SELECT	c.ie_auxiliar,
		coalesce(c.vl_posicao_auxiliar,0),
		c.ds_grau_participacao
	from	pls_grau_participacao	c,
		pls_conta		a
	where	a.nr_sequencia		= nr_seq_conta_p
	and	a.nr_seq_grau_partic	= c.nr_sequencia;

C02 CURSOR FOR
	SELECT	c.ie_auxiliar,
		coalesce(c.vl_posicao_auxiliar,0),
		c.ds_grau_participacao
	from	pls_proc_participante	b,
		pls_grau_participacao	c,
		pls_conta_proc		a
	where	a.nr_sequencia		= b.nr_seq_conta_proc
	and	b.nr_seq_grau_partic	= c.nr_sequencia
	and	a.nr_sequencia		= nr_seq_conta_proc_p
	and	coalesce(b.ie_status,'X')		<> 'C';

BEGIN

open C01;
loop
fetch C01 into
	ie_auxiliar_w,
	vl_posicao_auxiliar_w,
	ds_grau_participacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_auxiliar_w = 'S') and (vl_posicao_auxiliar_w > nr_auxiliares_p ) then
		ds_retorno_w := ds_retorno_w || ds_grau_participacao_w || ', ';
	end if;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	ie_auxiliar_w,
	vl_posicao_auxiliar_w,
	ds_grau_participacao_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	if (ie_auxiliar_w = 'S') and (vl_posicao_auxiliar_w > nr_auxiliares_p ) then
		ds_retorno_w := ds_retorno_w || ds_grau_participacao_w || ', ';
	end if;
	end;
end loop;
close C02;

ds_retorno_w:= substr(ds_retorno_w,1, length(ds_retorno_w) - 2);

if	((LENGTH(ds_retorno_w) - LENGTH(replace(ds_retorno_w, ',', ''))) > 1) then
	begin
	ds_retorno_w:= ds_retorno_w || ' não são permitidos para este item.';
	end;
else
	begin
	ds_retorno_w:= ds_retorno_w || ' não permitido para este item.';
	end;

end if;

return substr(ds_retorno_w, 1, 4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
