CREATE OR REPLACE FUNCTION pls_obter_ds_carencia (nr_seq_tipo_carencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_carencia_w varchar(255);


BEGIN
if (nr_seq_tipo_carencia_p IS NOT NULL AND nr_seq_tipo_carencia_p::text <> '') then
	begin
	select	substr(ds_carencia,1,255)
	into STRICT	ds_carencia_w
	from	pls_tipo_carencia
	where	nr_sequencia = nr_seq_tipo_carencia_p
	and	ie_cpt = 'N';
	end;
end if;

return	ds_carencia_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
