CREATE OR REPLACE FUNCTION pls_obter_ds_indic_clinica ( ds_parametros_p text, nr_seq_atendimento_p bigint, cd_doenca_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_indicacao_clinica_w	varchar(4000);
ie_existe_param_atend_w	varchar(2);


BEGIN
if (ds_parametros_p IS NOT NULL AND ds_parametros_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (cd_doenca_p IS NOT NULL AND cd_doenca_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin

	select	pls_obter_se_item_lista(ds_parametros_p, lpad(pls_obter_cd_tipo_atend_tiss(nr_seq_atendimento_p, cd_estabelecimento_p),2,0))
	into STRICT	ie_existe_param_atend_w
	;

	if (ie_existe_param_atend_w = 'S') then
		begin

		select	substr(obter_desc_cid(cd_doenca_p),1,255)
		into STRICT	ds_indicacao_clinica_w
		;

		end;
	end if;
	end;
end if;

return	ds_indicacao_clinica_w;

end;
$body$
