CREATE OR REPLACE FUNCTION pls_obter_dt_carencia ( ie_consistir_caren_prod_ant_p text, nr_seq_segurado_ant_p pls_segurado.nr_sequencia%type, dt_contratacao_p pls_segurado.dt_contratacao%type, dt_alteracao_ant_p pls_segurado_alt_plano.dt_alteracao%type, qt_dias_fora_abrang_ant_p pls_carencia.qt_dias_fora_abrang_ant%type, qt_dias_p pls_carencia.qt_dias%type, dt_inicio_vigencia_p pls_carencia.dt_inicio_vigencia%type, dt_inclusao_operadora_p pls_segurado.dt_inclusao_operadora%type) RETURNS PLS_SEGURADO_ALT_PLANO.DT_ALTERACAO%TYPE AS $body$
