CREATE OR REPLACE FUNCTION pls_obter_dt_consist_pessoa (nr_seq_lote_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp	:= null;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	max(a.dt_consistente)
		into STRICT	dt_retorno_w
		from	pls_pessoa_inconsistente a
		where	a.nr_seq_lote	= nr_seq_lote_p
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	not exists (SELECT	1
					from	pls_pessoa_inconsistente x
					where	x.nr_seq_lote	= a.nr_seq_lote
					and	x.cd_pessoa_fisica = a.cd_pessoa_fisica
					and	coalesce(x.dt_consistente::text, '') = '');
	else
		select	max(a.dt_consistente)
		into STRICT	dt_retorno_w
		from	pls_pessoa_inconsistente a
		where	a.nr_seq_lote	= nr_seq_lote_p
		and	a.cd_cgc 	= cd_cgc_p
		and	not exists (SELECT	1
					from	pls_pessoa_inconsistente x
					where	x.nr_seq_lote	= a.nr_seq_lote
					and	x.cd_cgc	= a.cd_cgc
					and	coalesce(x.dt_consistente::text, '') = '');
	end if;
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
