CREATE OR REPLACE FUNCTION pls_obter_dt_emissao_conta (nr_seq_analise_p bigint) RETURNS varchar AS $body$
DECLARE


dt_emissao_w	varchar(10)	:= '';


BEGIN
if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then
	select	to_char(max(a.dt_atendimento_referencia),'dd/mm/yyyy')
	into STRICT	dt_emissao_w
	from 	pls_conta a,
		pls_analise_conta b
	where
	a.nr_seq_analise 	= b.nr_sequencia
	and a.nr_seq_analise 	= nr_seq_analise_p;
end if;

return dt_emissao_w;

end;
