SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dt_faturas_abertas ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

 
dt_referencia_w		timestamp;
ds_data_w		varchar(10);
ds_faturas_abertas_w	varchar(1000);
			
C01 CURSOR FOR 
	SELECT	distinct trunc(c.dt_mesano_referencia, 'month') 
	from	titulo_receber		a, 
		pls_fatura		b, 
		pls_lote_faturamento	c 
	where (b.nr_titulo = a.nr_titulo or b.nr_titulo_ndc = a.nr_titulo) 
	and	c.nr_sequencia = b.nr_seq_lote 
	and (a.cd_pessoa_fisica = cd_pessoa_fisica_p or a.cd_cgc = cd_cgc_p) 
	and	a.ie_situacao = '1' 
	and	clock_timestamp() >= a.dt_vencimento 
	and	trunc(c.dt_mesano_referencia, 'month') < trunc(clock_timestamp(), 'month') 
	order by 1;
	

BEGIN 
 
ds_faturas_abertas_w	:=	null;
 
open C01;
loop 
fetch C01 into	 
	dt_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ds_data_w := substr(to_char(dt_referencia_w, 'month'),1,3) || '/' || to_char(dt_referencia_w, 'yyyy');
	 
	ds_faturas_abertas_w := ds_faturas_abertas_w || ds_data_w || ', ';
	 
	end;
end loop;
close C01;
 
if (ds_faturas_abertas_w IS NOT NULL AND ds_faturas_abertas_w::text <> '') then 
	ds_faturas_abertas_w 	:= 	'ATENÇÃO: CONSTAM EM ABERTO ATÉ O PRESENTE MOMENTO, AS SEGUINTES FATURAS:' || chr(13) || 
					substr(ds_faturas_abertas_w, 1, (length(ds_faturas_abertas_w) - 2)) || chr(13) || 
					'FAVOR ENTRAR EM CONTATO COM O SETOR FINANCEIRO.';
end if;
 
return	ds_faturas_abertas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dt_faturas_abertas ( cd_pessoa_fisica_p text, cd_cgc_p text) FROM PUBLIC;

