CREATE OR REPLACE FUNCTION pls_obter_dt_limite_espec_ans ( dt_referencia_p timestamp, cd_especialidade_p bigint, cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_dias_cons_ans_w	especialidade_medica.qt_dias_cons_ans%type	:= 0;
dt_retorno_w		timestamp := null;


BEGIN
if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then
	select 	max(coalesce(qt_dias_cons_ans,0))
	into STRICT 	qt_dias_cons_ans_w
	from 	especialidade_medica
	where	cd_especialidade = cd_especialidade_p;

	if (qt_dias_cons_ans_w > 0) then
		dt_retorno_w	:= obter_dias_uteis_periodo_rn259(dt_referencia_p,cd_especialidade_p,cd_estabelecimento_p);
	end if;
end if;

return	dt_retorno_w;

end;
