SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dt_parcelas_abertas ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


dt_referencia_w		timestamp;
ds_data_w		varchar(10);
ds_parcelas_abertas_w	varchar(500);

C01 CURSOR FOR
	SELECT	distinct a.dt_referencia
	from	pls_mensalidade	a,
		titulo_receber	b
	where	a.nr_sequencia 		= b.nr_seq_mensalidade
	and (b.cd_pessoa_fisica	= cd_pessoa_fisica_p
	or	b.cd_cgc		= cd_cgc_p)
	and	b.ie_situacao		= '1'
	and	clock_timestamp() >= b.dt_vencimento
	order by a.dt_referencia;


BEGIN

ds_parcelas_abertas_w	:=	null;

open C01;
loop
fetch C01 into
	dt_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_data_w := substr(to_char(dt_referencia_w, 'month'),1,3)  || '/' || to_char(dt_referencia_w, 'yyyy');

	ds_parcelas_abertas_w := ds_parcelas_abertas_w || ds_data_w || ', ';

	end;
end loop;
close C01;

if (ds_parcelas_abertas_w IS NOT NULL AND ds_parcelas_abertas_w::text <> '') then
	ds_parcelas_abertas_w 	:= 	'ATENÇÃO: CONSTAM EM ABERTO ATÉ O PRESENTE MOMENTO, AS SEGUINTES PARCELAS:' || chr(13) ||
					substr(ds_parcelas_abertas_w, 1, (length(ds_parcelas_abertas_w) - 2)) || chr(13) ||
					'FAVOR ENTRAR EM CONTATO COM O SETOR FINANCEIRO.' || chr(13) ||
					'CASO AS PARCELAS CITADAS ESTEJAM PAGAS, DESCONSIDERAR ESTE AVISO.';
end if;

return	ds_parcelas_abertas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_dt_parcelas_abertas ( cd_pessoa_fisica_p text, cd_cgc_p text) FROM PUBLIC;

