CREATE OR REPLACE FUNCTION pls_obter_dt_reajuste_contrato ( nr_seq_contrato_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w	timestamp;


BEGIN

select	max(b.dt_reajuste)
into STRICT	ds_retorno_w
from	pls_contrato_grupo	a,
	pls_grupo_contrato	b
where	a.nr_seq_grupo		= b.nr_sequencia
and	a.ie_reajuste_grupo	= 'S'
and	a.nr_seq_contrato	= nr_seq_contrato_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
