CREATE OR REPLACE FUNCTION pls_obter_dt_reaj_contrato ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, dt_inicio_vigencia_p pls_grupo_contrato.dt_inicio_vigencia%type, dt_fim_vigencia_p pls_grupo_contrato.dt_fim_vigencia%type) RETURNS timestamp AS $body$
DECLARE


dt_mes_reajuste_w	timestamp;
nr_mes_reajuste_w	pls_contrato.nr_mes_reajuste%type;


BEGIN

dt_mes_reajuste_w	:= null;

if	((nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') and (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and (dt_fim_vigencia_p IS NOT NULL AND dt_fim_vigencia_p::text <> '')) then

	select	max(nr_mes_reajuste)
	into STRICT	nr_mes_reajuste_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_p;

	if (nr_mes_reajuste_w IS NOT NULL AND nr_mes_reajuste_w::text <> '') then
		dt_mes_reajuste_w	:= to_date('01/'||to_char(lpad(nr_mes_reajuste_w, 2, '0'))||to_char(dt_inicio_vigencia_p,'yyyy'));

		if (not dt_mes_reajuste_w between dt_inicio_vigencia_p and dt_fim_vigencia_p) then
			dt_mes_reajuste_w	:= to_date('01/'||to_char(lpad(nr_mes_reajuste_w, 2, '0'))||(to_char(dt_inicio_vigencia_p,'yyyy')+1));
		end if;
	end if;
end if;

return	dt_mes_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
