SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_dt_refer_lanc_adic ( nr_seq_vendedor_p bigint, nr_seq_repasse_lanc_p bigint, ie_origem_lanc_p text) RETURNS timestamp AS $body$
DECLARE


/* ie_origem_lanc:
	R	= Repasse
	C	= Controle
*/
dt_retorno_w	timestamp;


BEGIN

if (ie_origem_lanc_p = 'R') then

	begin
	select	a.dt_referencia
	into STRICT	dt_retorno_w
	from	pls_repasse_lanc	b,
		pls_repasse_vend	a
	where	a.nr_sequencia 		= b.nr_seq_repasse
	and	a.nr_seq_vendedor 	= nr_seq_vendedor_p
	and	b.nr_sequencia 		= nr_seq_repasse_lanc_p;
	exception
	when others then
		dt_retorno_w := null;
	end;

elsif (ie_origem_lanc_p = 'C') then

	begin
	select	a.dt_referencia
	into STRICT	dt_retorno_w
	from	pls_repasse_lanc	c,
		pls_comissao		b,
		pls_lote_comissao	a
	where	a.nr_sequencia		= b.nr_seq_lote
	and	b.nr_sequencia 		= c.nr_seq_comissao
	and	b.nr_seq_canal_venda 	= nr_seq_vendedor_p
	and	c.nr_sequencia 		= nr_seq_repasse_lanc_p;
	exception
	when others then
		dt_retorno_w := null;
	end;

end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
