CREATE OR REPLACE FUNCTION pls_obter_dt_suspensao_seg (nr_seq_segurado_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp	:= null;


BEGIN

if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	max(trunc(a.dt_inicio_suspensao))
	into STRICT	dt_retorno_w
	from	pls_segurado_suspensao a
	where	a.nr_seq_segurado	= nr_seq_segurado_p
	and	coalesce(a.dt_fim_suspensao::text, '') = '';

	if (coalesce(dt_retorno_w::text, '') = '') then
		select	max(trunc(a.dt_inicio_suspensao))
		into STRICT	dt_retorno_w
		from	pls_segurado_suspensao 	a,
			pls_segurado		b
		where	a.nr_seq_contrato 	= b.nr_seq_contrato
		and	b.nr_sequencia		= nr_seq_segurado_p
		and	coalesce(a.dt_fim_suspensao::text, '') = '';
	end if;

	if (coalesce(dt_retorno_w::text, '') = '') then
		select	max(trunc(a.dt_inicio_suspensao))
		into STRICT	dt_retorno_w
		from	pls_segurado_suspensao 	a,
			pls_segurado		b
		where	a.nr_seq_pagador 	= b.nr_seq_pagador
		and	b.nr_sequencia		= nr_seq_segurado_p
		and	coalesce(a.dt_fim_suspensao::text, '') = '';
	end if;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
